        function FWS_TableRowSelector(rowIDPrefix,cbIDPrefix,defClass,selClass) {this.rowIDPrefix =rowIDPrefix; this.cbIDPrefix =cbIDPrefix; this.disabledRows =new Array(); this.selectedRows =new Array(); this.lastHoverRow =-1; 	if(typeof defClass =='undefined')this.defClass ='hlCol';else this.defClass =defClass; 	if(typeof selClass =='undefined')this.selClass ='hlCol_sel';else this.selClass =selClass;     this.setRowStyle =function(index,row,style) {	if(style !='sel'&&this.selectedRows[index]) style ='sel';for(var i =0;i <row.childNodes.length;i++) {	if(row.childNodes[i].nodeName.toLowerCase() =='td'){	if(row.childNodes[i].style) this.setColStyle(row.childNodes[i],style); }}};this.setColStyle =function(column,style) {  	if(column.className ==this.defClass ||column.className ==this.selClass) {switch(style) {case 'def':column.className =this.defClass; break; case 'sel':column.className =this.selClass; break; }} for(var i =0;i <column.childNodes.length;i++) {	if(column.childNodes[i].hasChildNodes()) this.setColStyle(column.childNodes[i],style); }};this.toggleRowSelected =toggleRowSelected; this.toggleAllSelected =toggleAllSelected; this.setRowDisabled =setRowDisabled; this.setRowEnabled =setRowEnabled; this.toggleRows =toggleRows; }  function setRowDisabled(row) {this.disabledRows[row] =true; }  function setRowEnabled(row) {this.disabledRows[row] =false; }   function toggleRows(indices) {var aindices = indices.split(','); for(var i =0;i <aindices.length;i++) {var index =parseInt(aindices[i]); 	if(index >=0)this.toggleRowSelected(index); }}   function toggleRowSelected(index) {	if(this.disabledRows[index]) return; var cb=null; 	if(this.cbIDPrefix) cb=FWS_getElement(this.cbIDPrefix + index); var row =FWS_getElement(this.rowIDPrefix + index);  	if(this.selectedRows[index]) {this.selectedRows[index] =false; this.setRowStyle(index,row,'def'); 	if(this.cbIDPrefix) cb.checked =false; }else {this.selectedRows[index] =true; this.setRowStyle(index,row,'sel'); 	if(this.cbIDPrefix) cb.checked =true; }} function toggleAllSelected() {for(var i =0;;i++) {var row =FWS_getElement(this.rowIDPrefix + i); var enabled =!this.disabledRows[i]; 	if(row &&enabled) this.toggleRowSelected(i); else if(enabled) break; }}