          function BS_getPostFormAJAXConstr(postFormID,fieldID,path,requestURL) {this.postFormID =postFormID; this.bbcFieldID =fieldID; this.requestURL =requestURL; this.path =path; this.type ='';this.getPostForm =getPostForm; }function getPostForm(type) {this.type =type; var url =this.requestURL.replace(/__MODE__/,type); var self =this; myAjax.sendPostRequest(url,"bspath=" +this.path,function(text) {var form =document.getElementById(self.postFormID); var field =document.getElementById(self.bbcFieldID); 	if(field !=null &&form !=null) {var ftext ='';	if(self.type =='applet')ftext =field.value; else if(field.toString ||typeof field.toString =='function')ftext =field.toString(); form.innerHTML =text; var myint =window.setInterval(function() {field =document.getElementById(self.bbcFieldID);  	if(field !=null &&(self.type !='applet'||typeof field.insertText =='function')){	if(self.type =='applet')field.insertText(ftext); else field.value =ftext; window.clearInterval(myint); }},50);}});}