          function BS_quoteMessageAJAX(number,fieldID,requestURL) {this.number =number; this.bbcFieldID =fieldID; this.requestURL =requestURL; this.quoteMessage =quoteMessage; }  function quoteMessage(mID) {var self =this; var url =this.requestURL.replace(/__ID__/,mID); myAjax.sendGetRequest(url,function(text) {var field =document.getElementById(self.bbcFieldID); 	if(field !=null) {	if(BBCodeModes[self.number] =='applet'){var applet =document.getElementById(self.bbcFieldID); applet.insertText(text); }else _insertAtCursor(self.bbcFieldID,"\n" +text +"\n");}});}